Connect-MsolService
$Result = Get-msoluser -All | Where { $_.isLicensed -eq $True } | select DisplayName, UserPrincipalName, @{N = 'Last Password Reset'; E = { ($_.LastPasswordChangeTimestamp) } }, @{N = 'Blocked Login'; E = { ($_.BlockCredential) } }, @{N = 'MFA State'; E = { ($_.StrongAuthenticationRequirements.State) } } | Sort-Object DisplayName
$Result | Export-Csv -Path C:\Temp\PasswordResetReport.csv